function uvstm_accordion(){const accordion=document.querySelectorAll('.uvstm-accordion');if(accordion){const accordionTriggers=document.querySelectorAll('.uvstm-accordion-item_trigger');const accordionContent=document.querySelectorAll('.uvstm-accordion-item_content');accordionTriggers.forEach((el,i)=>{el.setAttribute('id',`uvstm-accordion-triggerID-${i}`);el.setAttribute('aria-controls',`uvstm-accordion-contentID-${i}`)})
accordionContent.forEach((el,i)=>{el.setAttribute('id',`uvstm-accordion-contentID-${i}`);el.setAttribute('aria-labelledby',`uvstm-accordion-triggerID-${i}`)})
accordionTriggers.forEach(el=>{el.addEventListener('click',(e)=>{const self=e.currentTarget;const parent=self.closest('.uvstm-accordion-item');const content=parent.querySelector('.uvstm-accordion-item_content');const getContentPaddings=window.getComputedStyle(self,null).padding.split(' ');const accordionMode=parent.closest('.uvstm-accordion').getAttribute('data-uvstm-accordion-mode');let paddingTB=(getContentPaddings.length>1)?+getContentPaddings[1].replace('px','')+ +getContentPaddings[3].replace('px',''):0;if(!parent.classList.contains('uvstm-accordion-item--open')){parent.classList.add('uvstm-accordion-item--open');self.setAttribute('aria-expanded',!0);content.setAttribute('aria-hidden',!1);content.style.maxHeight=content.scrollHeight+paddingTB+'px'}else{parent.classList.remove('uvstm-accordion-item--open');self.setAttribute('aria-expanded',!1);content.setAttribute('aria-hidden',!0);content.style.maxHeight=null}})})}}