class UvstmTabs{constructor(){document.querySelectorAll('.uvstm-tabs-control_item').forEach((el,i)=>{el.setAttribute('id',`uvstm-tabs-controlID--${i}`);el.setAttribute('data-uvstm-tabs-trigger',`uvstm-tabs-contentID--${i}`);el.setAttribute('aria-controls',`uvstm-tabs-contentID--${i}`);el.setAttribute('aria-selected',!1)})
document.querySelectorAll('.uvstm-tabs-content_item').forEach((el,i)=>{el.setAttribute('id',`uvstm-tabs-contentID--${i}`);el.setAttribute('aria-labelledby',`uvstm-tabs-controlID--${i}`);el.setAttribute('aria-hidden',!0)})}
init(el,params={initTab:0,preventHeightJump:!1}){let{initTab,preventHeightJump}=params;initTab-=1;this.el=document.querySelector(el);const control=this.el.querySelector('.uvstm-tabs-control');const controlItems=control.querySelectorAll('.uvstm-tabs-control_item');const content=this.el.querySelector('.uvstm-tabs-content');const contentItems=content.querySelectorAll('.uvstm-tabs-content_item');controlItems[initTab].classList.add('uvstm-tabs-control_item--active');contentItems[initTab].classList.add('uvstm-tabs-content_item--active');controlItems[initTab].setAttribute('aria-selected',!0);contentItems[initTab].setAttribute('aria-hidden',!1);if(preventHeightJump){let contentMaxHeight=0;contentItems.forEach(el=>{if(contentMaxHeight<el.offsetHeight){contentMaxHeight=el.offsetHeight}})
content.setAttribute('style',`height: ${contentMaxHeight}px;`)}
control.addEventListener('click',e=>{handler(e)})
control.addEventListener('keydown',e=>{if(e.code=='Enter'){handler(e)}})
function handler(e){const self=e.target;const controlItem=self.closest('.uvstm-tabs-control_item');const trigger=controlItem.getAttribute('data-uvstm-tabs-trigger');const contentItem=document.querySelector(`#${trigger}`);Array.from(control.children).forEach(el=>{el.classList.remove('uvstm-tabs-control_item--active');el.setAttribute('aria-selected',!1)})
Array.from(content.children).forEach(el=>{el.classList.remove('uvstm-tabs-content_item--active');el.setAttribute('aria-hidden',!0)})
controlItem.classList.add('uvstm-tabs-control_item--active');contentItem.classList.add('uvstm-tabs-content_item--active');controlItem.setAttribute('aria-selected',!0);contentItem.setAttribute('aria-hidden',!1);if(!preventHeightJump){content.setAttribute('style',`height: ${contentItem.offsetHeight}px;`)}}}}